const fetch = require('node-fetch');

// --- Configuration ---
// $baseUrl, $userUuid, $prompt, $sessionId come from Input Variables

// --- Helper Function for Polling ---
const sleep = (ms) => new Promise(resolve => setTimeout(resolve, ms));

const HEADERS = {
    'Content-Type': 'application/json',
    'X-TDA-User-UUID': $userUuid
};

try {
    let activeSessionId = $sessionId;

    // === STEP 1: Check or Create Session ===
    // If no session ID is provided, we create one.
    if (!activeSessionId || activeSessionId.trim() === "") {
        const sessionResp = await fetch(`${$baseUrl}/api/v1/sessions`, {
            method: 'POST',
            headers: HEADERS
        });

        if (!sessionResp.ok) throw new Error(`Session Create Failed: ${sessionResp.statusText}`);
        const sessionData = await sessionResp.json();
        activeSessionId = sessionData.session_id;

        // Optional: Log that we created a new session
        // console.log(`Created new session: ${activeSessionId}`);
    }

    // === STEP 2: Submit the Query (API Section 3.3) ===
    // We use activeSessionId (either the one provided or the one we just created)
    const queryResponse = await fetch(`${$baseUrl}/api/v1/sessions/${activeSessionId}/query`, {
        method: 'POST',
        headers: HEADERS,
        body: JSON.stringify({
            prompt: $prompt 
        })
    });

    // Handle invalid session ID case (e.g. expired session provided)
    if (queryResponse.status === 404) {
        throw new Error(`Session ${activeSessionId} not found or expired. Please try again without a session ID.`);
    }

    if (queryResponse.status !== 202) {
        throw new Error(`Failed to submit query: ${queryResponse.statusText}`);
    }

    const taskData = await queryResponse.json();
    const statusUrl = taskData.status_url; 

    // === STEP 3: Poll for the Result (API Section 3.4) ===
    let taskComplete = false;
    let finalResult = null;
    let pollCount = 0;
    const maxPolls = 60; 

    while (!taskComplete && pollCount < maxPolls) {
        // Construct full URL using baseUrl + relative statusUrl
        const statusResponse = await fetch(`${$baseUrl}${statusUrl}`, {
            headers: HEADERS
        });

        const statusData = await statusResponse.json();

        switch (statusData.status) {
            case 'complete':
                taskComplete = true;
                finalResult = statusData.result;
                break;
            case 'error':
                throw new Error(`Agent Error: ${JSON.stringify(statusData.result)}`);
            case 'cancelled':
                throw new Error('Task was cancelled');
            case 'pending':
            case 'processing':
                pollCount++;
                await sleep(2000);
                break;
            default:
                throw new Error(`Unknown status: ${statusData.status}`);
        }
    }

    if (!finalResult) throw new Error("Task timed out.");

    // === STEP 4: Return the Answer ===
    if (finalResult.direct_answer) {
        return finalResult.direct_answer;
    } else if (finalResult.executive_summary) {
        return finalResult.executive_summary;
    } else {
        return JSON.stringify(finalResult);
    }

} catch (e) {
    return `Error: ${e.message}`;
}

